import { useParams } from 'react-router-dom';

export const ItemDetailPage = () => {
  const { id } = useParams();
  const selectedItem = item.find((item) => item.id === id);
  return (
    <div>
      <h2>{selectedItem.name}</h2>
      <img
        src={selectedItem.image}
        alt={selectedItem.name}
        style={{ width: '300px' }}
      />
    </div>
  );
};
